@MP_Cost: lbu r2,0x0090(r16) #Load Attacker's support set nop # andi r2,r2,0x0080 # beq r2,r0, @MP_Cost.no_HoMP #Branch if Attacker does not have Half of MP ori r2,r0,0x0080 # mult r2,r4 #Variable * MP Cost mflo r2 # srl r4,r2,0x08 #MP Cost * Variable / 256 @MP_Cost.no_HoMP: lbu r2,0x0090(r16) #Load Attacker's support set nop # andi r2,r2,0x0040 # beq r2,r0, @MP_Cost.no_turbo_mp #Branch if Attacker does not have turbo mp ori r2,r0,0x0200 # mult r2,r4 #Variable * MP Cost mflo r2 # srl r4,r2,0x08 #MP Cost * Variable / 256 @MP_Cost.no_turbo_mp: lbu r2,0x0090(r16) #Load Attacker's Support Set nop # andi r2,r2,0x0020 # beq r2,r0, @MP_Cost.no_mp_support #Branch if Attacker does not have "no mp" nop # j @MP_Cost.end #Jump to end addu r4,r0,r0 #Clear mp cost @MP_Cost.no_mp_support: lbu r2,0x0090(r16) #Load Attacker's Support lh r3,0x002c(r16) #Load Attacker's Current MP andi r2,r2,0x0010 # beq r2,r0,@MP_Cost.end #Branch if Attacker does not have Blood Price ori r2,r2,0x0100 # bne r3,r0, @MP_Cost.end #Branch to end if attacker has MP (Blood Price only works when out of MP) nop # mult r2,r4 #Variable * MP Cost mflo r2 # srl r4,r2,0x08 #MP Cost * Variable / 256 ori r1,r0,0x0001 # @MP_Cost.end: jr r31 # nop #
@MPDisplayPrep: lbu r2,0x0090(r16) #Load Attacker Supports addu r8,r0,r0 #Clear r8 andi r2,r2,0x0080 # beq r2,r0, @MPDisplayPrep.no_HoMP #Branch if no half of mp nop # ori r8,r0,0x0001 #r8 = 1 @MPDisplayPrep.no_HoMP: lbu r2,0x0090(r16) #Load Attacker Supports nop # andi r2,r2,0x0040 # beq r2,r0, @MPDisplayPrep.no_turbo_mp #Branch if no turbo mp support nop # ori r8,r8,0x0002 # Combine r8 flags @MPDisplayPrep.no_turbo_mp: lbu r2,0x0090(r16) #Load Attacker Supports nop # andi r2,r2,0x0020 # beq r2,r0, @MPDisplayPrep.no_mp_support #Branch if no "no mp" support nop # j @MPDisplayPrep.end #Jump to end ori r8,r8,0x0004 #Combine r8 flags @MPDisplayPrep.no_mp_support: lbu r2,0x0090(r16) #Load Attacker Supports nop # andi r2,r2,0x0010 # beq r2,r0, @MPDisplayPrep.end #Branch if no blood price nop # ori r8,r8,0x00008 # Combine r8 flags @MPDisplayPrep.end: jr r31 # nop #
@MPDisplay: lw r8,0x00f8(r29) #Load MP Cost Supports nop # andi r2,r8,0x0001 # beq r2,r0, @MPDisplay.no_HoMP #Branch if no half of mp ori r2,r0,0x0080 # mult r2,r17 #Cost * Variable mflo r2 # srl r17,r2,0x08 #Cost * Variable / 256 @MPDisplay.no_HoMP: andi r2,r8,0x0002 # beq r2,r0, @MPDisplay.no_turbo_mp # ori r2,r0,0x0200 # mult r2,r17 #Cost * Variable mflo r2 # srl r17,r2,0x08 #Cost * Variable / 256 @MPDisplay.no_turbo_mp: andi r2,r8,0x0004 # beq r2,r0, @MPDisplay.no_mp_support #Branch if no "no mp" support nop # j @MPDisplay.end #Jump to end addu r17,r0,r0 #Clear MP Cost @MPDisplay.no_mp_support: andi r2,r8,0x0008 # beq r2,r0, @MPDisplay.end # ori r2,r0,0x0100 # mult r2,r17 #Cost * Variable mflo r2 # srl r17,r2,0x08 #Cost * Variable / 256 @MPDisplay.end: jr r31 # nop #
@RedDisplay: lbu r2,0x0090(r22) #Load 2nd set of Support lbu r3,0x01ec(r4) #Load MP Cost andi r2,r2,0x0010 # beq r2,r0, @RedDisplay.load_mp #Branch if user does not have Blood Price nop # lhu r2,0x002c(r22) #Load Current MP nop # slt r1,r3,r2 #if MP Cost less then Current MP, r1 = 1 bne r1,r0, @RedDisplay.end #Branch to end if current mp is greater then mp cost nop # lhu r2,0x0028(r22) #Load Target Current HP j @RedDisplay.end #END nop # @RedDisplay.load_mp: lhu r2,0x002c(r22) #Load Current MP @RedDisplay.end: jr r31 # nop #
@Battle_Message: lbu r2,0x0090(r4) #Load 2nd set of Support nop # andi r2,r2,0x0010 # beq r2,r0, @Battle_Message.load_mp #Branch if user does not have Blood Price nop # lhu r2,0x002C(r4) #Load Current MP nop # slt r1,r19,r2 # bne r1,r0, @Battle_Message.end # nop # lhu r2,0x0028(r4) # j @Battle_Message.end #End nop # @Battle_Message.load_mp: lhu r2,0x002c(r4) #Load Current MP @Battle_Message.end: jr r31 # nop #
@fix_hp_spent: addiu r29,r29,-0x0018 # sw r31,0x0010(r29) # lhu r16,0x002c(r17) #Load Current MP lhu r23,0x0028(r17) #Load Current HP jal 0x0017da20 #MP Cost routine addu r4,r17,r0 #r4 = Unit Data Pointer sh r16,0x002c(r17) # sh r23,0x0028(r17) # lw r31,0x0010(r29) # addiu r29,r29,0x0018 # jr r31 # nop #
@AI_MP_Supports: addiu r29,r29,-0x0020 # sw r31,0x0010(r29) # sw r3,0x0014(r29) # sw r16,0x0018(r29) # sw r17,0x001c(r29) # addu r17,r16,r0 #r17 = Ability Data lbu r4,0x0011(r17) #r4 = MP Cost jal @MP_Cost # addu r16,r18,r0 # sb r4,0x0011(r17) #Store MP Cost lw r31,0x0010(r29) # lw r3,0x0014(r29) # lw r16,0x0018(r29) # lw r17,0x001c(r29) # jr r31 # addiu r29,r29,0x0020 #
@AI_Knows_Blood_Price_Uses_HP: lbu r3,0x0090(r20) #Load Attacker's Support nop # andi r3,r3,0x0010 # beq r3,r0, @AI.end #Branch to end if not using Blood Price nop # lbu r3,0x0011(r22) #Load MP Cost nop # sltu r3,r3,r2 # bne r3,r0, @AI.end #Branch if enough MP to cast spell nop # lhu r2,0x0028(r22) #Load HP @AI.end: lbu r3,0x0011(r22) #Load MP Cost jr r31 # nop #
@Turbo_MP_Damage_Increase: # lw r3,0x2d94(r2) #r3 = Attacker Data ori r4,r0,0x0180 #r4 = Multiplier lbu r5,0x0090(r3) #Load Attacker Support Set lhu r6,0x38ce(r2) #Load XA andi r5,r5,0x0040 # beq r5,r0, @Turbo_MP.end #Branch if Turbo MP not equipped nop # mult r4,r6 #XA * Multiplier mflo r4 # srl r4,r4,0x08 #XA * Multiplier / 256 sh r4,0x38ce(r2) #Store new XA @Turbo_MP.end: lbu r2,0x0090(r3) #Load Attacker 2nd Support Set jr r31 # nop #

@Set_Dead_CT: lbu r2, 0x0005(r7) #Load Unit Battle Flags lbu r3, 0x0006(r7) #Load Unit Gender andi r2, r2, 0x0004 # bne r2, r0, @DS.dead_ct_immortal #Branch if Unit is Immortal andi r2, r3, 0x0009 # bne r2, r0, @DS.dead_ct_immortal # nop # ori r8, r0, 0x0003 #r8 = 3 @DS.dead_ct_immortal: jr r31 #Return sb r8, 0x0007(r7) #Store Dead Timer
@Death_Sentence: lbu r2, 0x006C(r7) #Load Death Sentence CT nop # beq r2, r0, @DS.no_ds #Branch if DS CT = 0 addu r2, r0, r0 #Clear r2 addiu r2, r0, -0x0001 #r2 = xFFFF if Death Sentence is being set @DS.no_ds: jr r31 # nop #
@Status_CT_RSM: lbu r4,0x0091(r7) Load Unit Supports # addu r3, r8, r0 # andi r4, r4, 0x0080 # beq r4, r0, @CT_RSM_no_boost #Branch if Support is not active nop # sll r4, r3, 0x01 # addu r4, r4, r3 # srl r3, r4, 0x01 #r3 = CT * 3 / 2 @CT_RSM_no_boost: lbu r4,0x0091(r7) #Load Unit Supports nop # andi r4, r4, 0x0040 # beq r4, r0, @CT_RSM_no_decrease #Branch if Support is not active nop # srl r3, r3, 0x01 #CT / 2 @CT_RSM_no_decrease: jr r31 # nop #
